    type_count[type]++;
    if (i===0){
        min_phase1[type]=max_phase1[type]=current_event.delta_phase1;
        min_phase2[type]=max_phase2[type]=current_event.delta_phase2;
        min_phase3[type]=max_phase3[type]=current_event.delta_phase3;
    } else {
        min_phase1[type]=(min_phase1[type] < current_event.delta_phase1 ? min_phase1[type] : current_event.delta_phase1);
        min_phase2[type]=(min_phase2[type] < current_event.delta_phase2 ? min_phase2[type] : current_event.delta_phase2);
