var now=Date.now();


// converts a cpu_in_counter (expressed in cs) into %
function convert_cpu_cs_to_percentage(cpu_in_counter,cpu_out_counter){
    //node.warn("convert_cpu_cs_to_percentage("+cpu_in_counter+","+cpu_out_counter+")");
    var cpu_counter = cpu_out_counter;
    var cpu_cs_counter = cpu_in_counter;
    var update_previous_counter_at_end = 1;
    if (msg.payload.hasOwnProperty(cpu_cs_counter)){
        var previous_counter = flow.get(msg.host+cpu_cs_counter) || -1;
        if (previous_counter != -1 ){
            delta_time   = now - previous_counter.time;
            delta_cpu_cs = msg.payload[cpu_cs_counter] - previous_counter.cpu_cs;
            // node.warn("[1]"+cpu_cs_counter +" :delta_cpu_cs =" + delta_cpu_cs + ", delta time=" + delta_time);
            // ignore if too long ago or too recent or delta_cpu is negative.
            if ( (delta_time >0) && (delta_time < 60*1000)){
                // don't update previous counter if not one minute passed.
                update_previous_counter_at_end = 0;
            } else if ( (delta_time > 20*60*1000) || (delta_time <= 0 ) || delta_cpu_cs < 0 ) {
                node.warn("previous counter ignored for " + process + " at host " + msg.host +
                         " :delta_cpu_cs =" + delta_cpu_cs + ", delta time=" + delta_time);
            } else {
                  msg.payload[cpu_counter] = +(delta_cpu_cs*1000/delta_time).toFixed(2);
            }
        }
        if (update_previous_counter_at_end){
            flow.set(msg.host + cpu_cs_counter, 
                {"time": now,  "cpu_cs": msg.payload[cpu_cs_counter] });
        }
        delete msg.payload[cpu_cs_counter];
        //node.warn("convert_cpu_cs_to_percentage:msg.payload["+cpu_counter+"]="+msg.payload[cpu_counter]);
    }
}

