            convert_cpu_cs_to_percentage(counter,general_counters[counter].outname);
        }
    }
}

// loop over all process counters
var process_counters=flow.get("static_configuration.hrSWRunTable");
for (var process in process_counters){
    if (process_counters.hasOwnProperty(process)){
        convert_cpu_cs_to_percentage(process + '_cpu_cs',process+'_cpu');
    }
}

return msg;

var process_counters = flow.get("static_configuration.hrSWRunTable");
for (var proc in process_counters){
    if (process_counters.hasOwnProperty(proc)){
        var proc_mem = proc + '_mem';
        if (msg.payload.hasOwnProperty(proc_mem)){
            msg.payload[proc_mem+'_MB']=Math.round(msg.payload[proc_mem]/1024);
            delete msg.payload[proc_mem];
        }
    }
}

var disk_counters = flow.get("static_configuration.dskTable");
for (var disk in disk_counters){
    if (disk_counters.hasOwnProperty(disk)){
        if (msg.payload.hasOwnProperty(disk)){
            msg.payload[disk+'_MB']=Math.round(msg.payload[disk]/1024);
            delete msg.payload[disk];
        }
    }
}

return msg;

var OIDs = "";
var OID2name = {};
var static_configuration = flow.get("static_configuration");
var separator="";

for (var name in static_configuration.general) {
  if (static_configuration.general.hasOwnProperty(name)){
      OID2name[static_configuration.general[name].OID]=name;
      OIDs = OIDs + separator + static_configuration.general[name].OID;
      separator =",";
  }
}

flow.set(msg.host, { "OIDs" : OIDs, "OID2name" : OID2name });

msg.payload = flow.get(msg.host);
return msg;

var dskTable=flow.get("static_configuration.dskTable");
var host=flow.get(msg.host);

// take care that /dev/root is appearing twice and should only be monitored once !!

// Available space on the device. For large lightly-used disks (>2Tb), this value will latch at INT32_MAX (2147483647).
var dskAvail_OID="1.3.6.1.4.1.2021.9.1.7.";  // device index must be appended to it.

// search key for Filesystem /dev/root and /dev/sda3  (= /mnt/wd1tb_media)
for (var name in dskTable){
//  if (dskTable(hasOwnProperty(name)) && ((dskTable[name][host]=="*")||(dskTable[name][host]==msg.host))){
    if (dskTable.hasOwnProperty(name) && ((dskTable[name].host=="*")||(dskTable[name].host==msg.host))){
      for (var key in msg.payload) {
       if (msg.payload.hasOwnProperty(key)) {
          // convert array buffer msg.payload[key][3] to a String
          if ( String.fromCharCode.apply(null, new Uint16Array(msg.payload[key][2])) == dskTable[name]["dskPath(2)"]){
            var OID = dskAvail_OID+key;
            host.OIDs=host.OIDs+"," + OID;
            host.OID2name[OID]=name;
            break;
          }
        }
      }
  }
}

flow.set(msg.host,host);
msg.payload=flow.get(msg.host);
delete msg.oid;
return msg;

var hrSWRunTable=flow.get("static_configuration.hrSWRunTable");
var host=flow.get(msg.host);

// hrSWRunPerfTable.
var hrSWRunName_OID="1.3.6.1.2.1.25.4.2.1.2." ; // device index must be appended to it.
var hrSWRunPerfMem_OID="1.3.6.1.2.1.25.5.1.1.2.";  // device index must be appended to it.
var hrSWRunPerfCPU_OID="1.3.6.1.2.1.25.5.1.1.1.";  // device index must be appended to it.


for (var name in hrSWRunTable){
    //node.warn("name = "+ name + "; host = "+ msg.host+ "hrSWRunTable[name].host="+ hrSWRunTable[name].host);
    if (hrSWRunTable.hasOwnProperty(name) && ((hrSWRunTable[name].host=="*")||(hrSWRunTable[name].host==msg.host))){
      for (var key in msg.payload) {
         if (msg.payload.hasOwnProperty(key)) {
             // convert array buffer msg.payload[key][2] to a String
             if ( ((String.fromCharCode.apply(null, new Uint16Array(msg.payload[key][2]))) == hrSWRunTable[name]["hrSWRunName(2)"]) &&
                  ((hrSWRunTable[name]["hrSWRunParams(5)"]=="*") || 
                   (hrSWRunTable[name]["hrSWRunParams(5)"]==((String.fromCharCode.apply(null, new Uint16Array(msg.payload[key][5]))))))){
                  var OID_hrSWRunName = hrSWRunName_OID+key;
                  var OID_Mem = hrSWRunPerfMem_OID+key;
                  var OID_CPU = hrSWRunPerfCPU_OID+key;
                  host.OIDs=host.OIDs+"," + OID_Mem + "," + OID_CPU + "," + OID_hrSWRunName;
                  host.OID2name[OID_hrSWRunName]=name+"_hrSWRunName";
                  host.OID2name[OID_Mem]=name+"_mem";
                  host.OID2name[OID_CPU]=name+"_cpu_cs";
                  break;
             }
         }
      }
    }
}

flow.set(msg.host,host);

msg.payload=flow.get(msg.host);
delete msg.oid;
return msg;

var process_counters = flow.get("static_configuration.hrSWRunTable");
for (var proc in process_counters){
    if (process_counters.hasOwnProperty(proc)){
        var proc_hrSWRunName = proc + '_hrSWRunName';
        var proc_mem         = proc + '_mem';
        var proc_cpu_cs      = proc + '_cpu_cs';
        if (msg.payload.hasOwnProperty(proc_hrSWRunName)){
            if (msg.payload[proc_hrSWRunName]!=process_counters[proc]["hrSWRunName(2)"]){
                node.warn("process name [="+msg.payload[proc_hrSWRunName]+"] doesn't match for process:" +proc);
                delete msg.payload[proc_mem];
                delete msg.payload[proc_cpu_cs];
            }
            delete msg.payload[proc_hrSWRunName];
        }
    }
}
return msg;

var ifTable=flow.get("static_configuration.ifTable");
var host=flow.get(msg.host);

// 	Total number of octets received on the interface, including framing characters 
var ifInOctets_OID="1.3.6.1.2.1.2.2.1.10.";  // device index must be appended to it.

// 	Total number of octets transmitted out of the interface, including framing characters.  
var ifOutOctets_OID="1.3.6.1.2.1.2.2.1.16.";  // device index must be appended to it.

// search key for Filesystem /dev/root and /dev/sda3  (= /mnt/wd1tb_media)
for (var name in ifTable){
    if (ifTable.hasOwnProperty(name) && ((ifTable[name].host=="*")||(ifTable[name].host==msg.host))){
      for (var key in msg.payload) {
       if (msg.payload.hasOwnProperty(key)) {
          // convert array buffer msg.payload[key][3] to a String
          if ( String.fromCharCode.apply(null, new Uint16Array(msg.payload[key][2])) == ifTable[name]["ifDescr(2)"]){
            var in_OID = ifInOctets_OID+key;
            var out_OID = ifOutOctets_OID+key;
            host.OIDs=host.OIDs+"," + in_OID + "," + out_OID;
            host.OID2name[in_OID] =name + '_InOctets';
            host.OID2name[out_OID]=name + '_OutOctets';
            break;
          }
        }
      }
  }
}

flow.set(msg.host,host);
msg.payload=flow.get(msg.host);
delete msg.oid;
return msg;

var now=Date.now();  // Return the number of milliseconds since 1970/01/01

var IF_counters=flow.get("static_configuration.ifTable");

// loop over all interfaces specified in static_configuration.ifTable
// IF = interface
for (var IF in IF_counters){
    if (IF_counters.hasOwnProperty(IF)){
        var IF_InOctets = IF+'_InOctets';
        var IF_OutOctets = IF + '_OutOctets';
        var update_previous_counter_at_end = 1;
        if (msg.payload.hasOwnProperty(IF_InOctets) && msg.payload.hasOwnProperty(IF_OutOctets)){
            var previous_counter = flow.get(msg.host+IF) || -1;
            if (previous_counter != -1 ){
                delta_time   = now - previous_counter.time;
                //node.warn("msg.payload["      + IF_InOctets + "]="+ msg.payload[IF_InOctets]);
                //node.warn("previous_counter[IF_InOctets]="+ previous_counter.IF_InOctets);
                delta_InOctets  = msg.payload[IF_InOctets] - previous_counter.IF_InOctets;
                delta_OutOctets = msg.payload[IF_OutOctets]- previous_counter.IF_OutOctets;
                //node.warn("[1]"+ IF + ", delta_time=" + delta_time +
                //         " :delta_InOctets =" + delta_InOctets + ", delta_OutOctets=" + delta_OutOctets);
                // ignore if too long ago or too recent or delta_cpu is negative.
                if ( (delta_time >0) && (delta_time < 60*1000)){
                    // don't update previous counter if not one minute passed.
                    update_previous_counter_at_end = 0;
                } else if ( (delta_time     > 20*60*1000) || (delta_time      <= 0 ) || 
                            (delta_InOctets < 0)          || (delta_OutOctets < 0  )) {
                    node.warn("previous counter ignored for " + IF + " at host " + msg.host +
                          " :delta_InOctets ="  + delta_InOctets  + 
                          " :delta_OutOctets =" + delta_OutOctets + ", delta time=" + delta_time);
                } else {
                   msg.payload[IF+'_inKBs']  = Math.round(delta_InOctets*1000/(delta_time*1024));
                   msg.payload[IF+'_outKBs'] = Math.round(delta_OutOctets*1000/(delta_time*1024));
                }
            }
            if (update_previous_counter_at_end){
               //node.warn("Updating flow context variable: " + msg.host + IF);
               flow.set(msg.host +IF, 
                  {"time": now,  
                   IF_InOctets : msg.payload[IF_InOctets], 
                   IF_OutOctets: msg.payload[IF_OutOctets] });
            }
            delete msg.payload[IF_InOctets];
            delete msg.payload[IF_OutOctets];
        }
    }
}

return msg;

// loop over all general process counters
var general_counters=flow.get("static_configuration.general");
for (var counter in general_counters){
    if (general_counters.hasOwnProperty(counter)){
        if (general_counters[counter].convert == "do not output"){
            delete msg.payload[counter];
        }
    }
}

return msg;

